import java.util.HashSet; import java.util.Scanner; 
public class Main { 
    public static void main(String[] args) { Scanner scanner = new Scanner(System.in); String input = scanner.nextLine(); String[] inputArray = input.split(" "); if (inputArray.length < 1 || inputArray.length > 10) { System.out.println("Array size must be between 1 and 10"); return; } int[] arr = new int[inputArray.length]; for (int i = 0; i < inputArray.length; i++) { try { arr[i] = Integer.parseInt(inputArray[i]); } catch (NumberFormatException e) { System.out.println("Array elements must be integers"); return; } if (arr[i] < -10 || arr[i] > 10) { System.out.println("Array elements must be from -10 to 10"); return; } } boolean hasZeroSum = hasZeroSumSubarray(arr); System.out.println(hasZeroSum); System.out.println(arr.length); } 
    private static boolean hasZeroSumSubarray(int[] arr) { HashSet<Integer> sumSet = new HashSet<>(); int cumulativeSum = 0; for (int value : arr) { cumulativeSum += value; if (cumulativeSum == 0 || sumSet.contains(cumulativeSum)) { return true; } sumSet.add(cumulativeSum); } return false; } }
                                                                                                                            