import java.util.Scanner; 
public class Main { public static void main(String[] args) { Scanner scanner = new Scanner(System.in); String input = scanner.nextLine(); if (input.length() < 4) { System.out.println("Provided input is less than 4, enter four digit integers"); return; } if (input.length() > 4) { System.out.println("Provided input is more than 4, enter four digit integers"); return; } try { int number = Integer.parseInt(input); if (number < 1000 || number > 9999) { System.out.println("Enter positive 4-digit integer"); return; } int[] digits = new int[4]; for (int i = 0; i < 4; i++) { digits[i] = (Character.getNumericValue(input.charAt(i)) + 5) % 10; } int encrypted1 = digits[2]; int encrypted2 = digits[3]; int encrypted3 = digits[0]; int encrypted4 = digits[1]; System.out.printf("%d%d%d%d%n", encrypted1, encrypted2, encrypted3, encrypted4); } catch (NumberFormatException e) { System.out.println("Enter only integer value"); } } }
                                                                                                                            